mapper.select:
SELECT APPEND_NM AS extraNm ,APPEND_TARGET AS extraTarget ,APPEND_COST AS extraCost ,REG_DT AS regDt FROM MIND_MGR_APPEND WHERE 1=1 AND USE_YN = 'Y'
AND HIGH_COMM_CD = #{highCommCd}
AND CENTER_SEQ = #{centerSeq}

SELECT COUNT(*) as cnt
FROM MIND_COMM_CD
WHERE 1=1
AND USE_YN = 'Y'

	AND HIGH_COMM_CD = #{highCommCd}


	AND COMM_NM = #{commNm}

mapper.update:
UPDATE MIND_BBS_DATA
	SET DISP_YN = #{dispYn}
WHERE DATA_SEQ = #{dataSeq}

mapper.select:
SELECT GROUP_CONCAT(COMM_NM)
  FROM MIND_CLIENT_COMM_CD
 WHERE USE_YN = 'Y'
   AND COMM_CD IN

   		#{item}


		AND CLIENT_CD = #{clientCd}

mapper.insert:
INSERT INTO MIND_COMM_CD
	(
		HIGH_COMM_CD
		, COMM_NM
		, OPTION_CD
		, USE_YN
		, COMM_DESC

		, ORDER_NO

		, REG_DT
	)
	VALUES (
		#{highCommCd}
		, #{commNm}
		, #{optionCd}
		, 'Y'
		, #{commDesc}

		, #{orderNo}

		, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.select:
SELECT comm_cd 
FROM mind_client_comm_cd
WHERE client_cd = #{clientCd}
AND comm_nm = #{commNm}

SELECT comm_cd 
FROM mind_comm_cd
WHERE comm_nm = #{commNm}

	AND option_cd = #{optionCd}

SELECT category_cd 
FROM mind_counsel_category
WHERE category_nm = #{categoryNm}

SELECT center_seq 
FROM mind_center
WHERE center_nm = #{categoryNm}

SELECT COMM_CD as commCd, CLIENT_CD as clientCd, HIGH_COMM_CD as highCommCd, COMM_NM as commNm, OPTION_CD as optionCd, USE_YN as useYn, LOGIN_YN as loginYn, POINT_USE_YN as pointUseYn, COMM_DESC as commDesc, REG_DT as regDt FROM MIND_CLIENT_COMM_CD
AND HIGH_COMM_CD = #{code}
AND HIGH_COMM_CD = (SELECT HIGH_COMM_CD FROM MIND_CLIENT_COMM_CD WHERE CLIENT_CD = #{clientCd} AND COMM_CD = #{commCd})
AND CLIENT_CD = #{clientCd}

mapper.update:
UPDATE MIND_USER
	SET USE_YN = #{useYn}
WHERE USER_KEY = #{userKey}

mapper.select:
SELECT START_DD AS startDd , END_DD AS endDd , PERIOD_NO AS periodNo , PERIOD_SEQ AS periodSeq FROM mind_client_jedo_period WHERE STATUS = 'Y'
AND client_cd = #{param1}
AND period_seq = #{param2}
ORDER BY period_seq desc
