mapper.delete:
DELETE FROM MIND_COUNSEL_FILE 
WHERE RECORD_CD = #{recordCd}
AND COUNSEL_CD = #{counselCd}
AND INTAKE_CD = #{intakeCd}
AND CENTER_SEQ = #{centerSeq}
AND CLIENT_CD = #{clientCd}
AND DOC_TYPE = #{docType}
AND DOC_STS = #{status}

mapper.insert:
		INSERT INTO MIND_COUNSEL_FILE (
			RECORD_CD
			, COUNSEL_CD
			, FILE_NM
			, FILE_PATH
			, DOC_TYPE
			, DOC_STS
			,COUNSEL_NM
			,APPR_STATUS
			,REG_DT
			,CLIENT_CD
			,CENTER_SEQ
			,INTAKE_CD
		) values (
			#{recordCd}
			, #{counselCd}
			, #{fileNm}
			, #{fileFullPath}
			, #{docType}
			, #{status}
			, #{counselNm}
			, '1'
			, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
			, #{clientCd}
			, #{centerSeq}
			, #{intakeCd}
		)		
		ON DUPLICATE KEY UPDATE 
    	FILE_NM = #{fileNm}
		,FILE_PATH = #{fileFullPath}
		,RECORD_CD = #{recordCd}
		,COUNSEL_CD = #{counselCd}
		,INTAKE_CD = #{intakeCd}
		,CENTER_SEQ = #{centerSeq}
		,CLIENT_CD = #{clientCd}
		,DOC_TYPE = #{docType}
		,DOC_STS = #{status}
		,REG_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')

mapper.update:
UPDATE MIND_COUNSEL_FILE SET 
	APPR_STATUS = #{chkInfo}
	,REG_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	,REG_ID = #{regId}
	,COUNSEL_NM = #{counselNm}

WHERE 1=1
AND DOC_TYPE = #{docType}
AND DOC_STS = #{status}
AND COUNSEL_CD = #{counselCd}
AND CLIENT_CD = #{clientCd}
AND CENTER_SEQ = #{centerSeq}
AND INTAKE_CD = #{intakeCd}
