mapper.select:
SELECT COUNT(1) 
  FROM MIND_DISPATCH_COUNSEL_INFO
 WHERE CLIENT_CD = #{clientCd}

SELECT COUNT(*) pageCommonCount FROM (
SELECT INFO_SEQ, CLIENT_CD, SUBJECT, LOGO_PATH, INFO_CONTENTS, CONTENTS, EMAIL, REG_ID, REG_DT FROM MIND_DISPATCH_COUNSEL_INFO WHERE 1 = 1 ORDER BY INFO_SEQ DESC
) A
LIMIT #{firstIndex} , #{pageSize}

mapper.update:
UPDATE MIND_DISPATCH_COUNSEL_INTAKE
SET 
	MEMO = #{memo},
  STATUS = #{status}
WHERE COUNSEL_INTAKE_SEQ = #{counselIntakeSeq}

UPDATE MIND_DISPATCH_COUNSEL_USER
SET USER_KEY = #{userKey}
WHERE COUNSEL_USER_SEQ = #{counselUserSeq}
