mapper.insert:
INSERT INTO MIND_EDU_COMPLETE (
	    EDU_TYPE,
	    EDU_DIV,
	    EDU_NM,
	    EDU_HOUR,
	    EDU_START_DT,
	    EDU_END_DT,
	    EDU_PLACE,
	    EDU_ETC,
	    USER_ID,
	    CENTER_SEQ	
) VALUES ( 
	#{eduType}
	, #{eduDiv}
	, #{eduNm}
	, #{eduHour}
	, #{eduDtFm}
	, #{eduDtTo}
	, #{eduPlace}
	, #{eduEtc}
	, #{userId}
	, #{centerSeq2}
) ON DUPLICATE KEY UPDATE 
	EDU_TYPE = #{eduType}
	, EDU_DIV = #{eduDiv}
	, EDU_NM = #{eduNm}
	, EDU_HOUR = #{eduHour}
	, EDU_START_DT = #{eduDtFm}
	, EDU_END_DT = #{eduDtTo}
	, EDU_PLACE = #{eduPlace}
	, EDU_ETC = #{eduEtc}
	, USER_ID = #{userId}
	, CENTER_SEQ = #{centerSeq2}

mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
select mei.CENTER_SEQ as centerSeq ,mei.EDU_SEQ as eduId , mei.edu_type as eduType , mei.EDU_DIV as eduDiv , mei.EDU_START_DT as eduDtFm , mei.EDU_END_DT as eduDtTo , mei.edu_etc as eduEtc , mei.edu_hour as eduHour , mei.EDU_PLACE as eduPlace , mei.EDU_NM as eduNm , mm.USER_NM as userNm , mm.USER_ID as userId , (select GENDER from mind_mgr_sub mms where mm.USER_ID = mms.USER_ID) as gender , (select CENTER_NM from mind_center mc where mm.CENTER_SEQ = mc.CENTER_SEQ )as centerNm from MIND_EDU_COMPLETE mei INNER JOIN mind_mgr mm ON mei.USER_ID = mm.USER_ID where 1=1 order by mei.EDU_SEQ desc
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
select mei.EDU_SEQ as eduId , mei.edu_type as eduType , mei.EDU_DIV as eduDiv , DATE_FORMAT(mei.EDU_START_DT,'%Y-%m-%d') as eduDtFm , DATE_FORMAT(mei.EDU_END_DT, '%Y-%m-%d') as eduDtTo , mei.edu_etc as eduEtc , mei.edu_hour as eduHour , mei.EDU_PLACE as eduPlace , mei.EDU_NM as eduNm , mm.USER_NM as userNm , mm.USER_ID as userId , (select GENDER from mind_mgr_sub mms where mm.USER_ID = mms.USER_ID) as gender , (select CENTER_NM from mind_center mc where mm.CENTER_SEQ = mc.CENTER_SEQ )as centerNm from MIND_EDU_COMPLETE mei INNER JOIN mind_mgr mm ON mei.USER_ID = mm.USER_ID where 1=1
AND mei.center_seq = #{centerSeq}
order by mei.EDU_SEQ desc
) A
LIMIT #{firstIndex} , #{pageSize}

