	, #{userId}
	, #{centerSeq}
)

mapper.update:
update MIND_EDU_COMPLETE
set 
	EDU_TYPE = #{eduType}
	, EDU_DIV = #{eduDiv}
	, EDU_NM = #{eduNm}
	, EDU_HOUR = #{eduHour}
	, EDU_START_DT = #{eduDtFm}
	, EDU_END_DT = #{eduDtTo}
	, EDU_PLACE = #{eduPlace}
	, EDU_ETC = #{eduEtc}
	, USER_ID = #{userId}
where EDU_SEQ = #{eduId}
