mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT (SELECT CLIENT_NM FROM MIND_CLIENT WHERE CLIENT_CD = A.CLIENT_CD) as clientCd ,C.COUNSEL_NM as counselNm ,C.MOBILE as mobile ,C.COUNSEL_DIV as counselDiv ,C.COUNSEL_TYPE as counselType ,DATE_FORMAT(CONVERT(B.YMD, DATE), '%Y-%m-%d') as ymd ,DATE_FORMAT(CONVERT(B.ST_TIME, TIME), '%i:%s') as stTime ,D.USER_NM as counselorNm ,A.COUNSEL_CD as counselCd FROM MIND_COUNSEL A, MIND_SCHEDULE B, MIND_COUNSEL_INTAKE C, MIND_MGR D WHERE 1=1 AND A.COUNSELOR_ID = #{counselorId} AND C.INTAKE_CD = A.INTAKE_CD AND A.COUNSEL_CD = B.COUNSEL_CD AND A.STATUS = '100520' AND A.COUNSELOR_ID = D.USER_ID AND B.SCHEDULE_TYPE IN ('100562', '100564') -- 게시판 상담은 제외하고 노출
AND C.COUNSEL_NM like CONCAT ( '%',#{counselNm},'%')
AND B.YMD >= #{startDt}
AND B.YMD <= #{endDt}
ORDER BY B.YMD DESC
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
SELECT (SELECT CLIENT_NM FROM MIND_CLIENT WHERE CLIENT_CD = A.CLIENT_CD) as clientCd ,C.COUNSEL_NM as counselNm ,C.MOBILE as mobile ,C.COUNSEL_DIV as counselDiv ,C.COUNSEL_TYPE as counselType ,DATE_FORMAT(CONVERT(B.YMD, DATE), '%Y-%m-%d') as ymd ,DATE_FORMAT(CONVERT(B.ST_TIME, TIME), '%i:%s') as stTime ,D.USER_NM as counselorNm ,A.COUNSEL_CD as counselCd FROM MIND_COUNSEL A, MIND_SCHEDULE B, MIND_COUNSEL_INTAKE C, MIND_MGR D WHERE 1=1 AND A.CENTER_SEQ = #{centerSeq} AND C.INTAKE_CD = A.INTAKE_CD AND A.COUNSEL_CD = B.COUNSEL_CD AND A.STATUS = '100520' AND A.COUNSELOR_ID = D.USER_ID AND B.SCHEDULE_TYPE IN ('100562', '100564') -- 게시판 상담은 제외하고 노출
AND C.COUNSEL_NM like CONCAT ( '%',#{counselNm},'%')
AND B.YMD >= #{startDt}
AND B.YMD <= #{endDt}
AND D.USER_NM like CONCAT ( '%',#{counselorNm},'%')
ORDER BY B.YMD DESC
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
SELECT (SELECT CLIENT_NM FROM MIND_CLIENT WHERE CLIENT_CD = A.CLIENT_CD) as clientNm
,CT.OWNER_NM as ownerNm
,C.COUNSEL_NM as counselNm ,AES_DECRYPT(UNHEX(C.MOBILE),'1234') as mobile ,C.COUNSEL_DIV as counselDiv ,C.COUNSEL_TYPE as counselType ,C.CHANNEL_TYPE as channelType ,DATE_FORMAT(CONVERT(B.YMD, DATE), '%Y-%m-%d') as ymd ,DATE_FORMAT(CONVERT(B.ST_TIME, TIME), '%i:%s') as stTime ,D.USER_NM as counselorNm ,A.COUNSEL_CD as counselCd ,A.COUNSELOR_ID as counselorId ,A.INTAKE_CD as intakeCd ,A.USER_KEY as userKey ,A.CENTER_SEQ as centerSeq ,A.CLIENT_CD as clientCd , (SELECT CENTER_NM FROM MIND_CENTER WHERE CENTER_SEQ = A.CENTER_SEQ) as centerNm , IF((SELECT ORDER_STATUS FROM MIND_ORDER WHERE COUNSEL_CD = A.COUNSEL_CD) = '100960', 'Y', 'N') as needPay ,A.RECORD_STATUS as recordStatus ,A.EXTENSION_STATUS as extensionStatus ,E.USER_NM as counselMgrNm ,C.COUNSEL_RESERV_CHK ,C.RELATION
,'counselor' AS orderByNm
,'date' AS orderByNm
,'ASC' AS orderByType
,'DESC' AS orderByType
FROM MIND_COUNSEL A LEFT JOIN (SELECT AA.USER_NM, BB.CLIENT_CD FROM MIND_MGR AA LEFT JOIN MIND_CLIENT BB ON AA.USER_ID = BB.COUNSEL_MGR_ID ) as E ON A.CLIENT_CD = E.CLIENT_CD, MIND_SCHEDULE B, MIND_COUNSEL_INTAKE C, MIND_MGR D
JOIN MIND_CENTER CT ON CT.CENTER_SEQ = #{centerSeq}
WHERE 1=1 AND A.COUNSELOR_ID = #{counselorId} AND C.INTAKE_CD = A.INTAKE_CD AND A.COUNSEL_CD = B.COUNSEL_CD AND A.STATUS in ('100520', '100521', '100884') AND A.COUNSELOR_ID = D.USER_ID AND A.COUNSEL_CD NOT IN ('91756', '105785')
AND C.COUNSEL_NM like CONCAT ( '%',#{counselNm},'%')
AND B.YMD >= #{startDt}
AND B.YMD <= #{endDt}
AND A.CLIENT_CD = #{clientCd}
AND CT.OWNER_NM = #{ownerNm}
AND A.COUNSEL_CD = #{counselCd}
C.COUNSEL_NM
A.SCHEDULE_DT
ASC
DESC
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
SELECT (SELECT CLIENT_NM FROM MIND_CLIENT WHERE CLIENT_CD = A.CLIENT_CD) as clientNm ,C.COUNSEL_NM as counselNm
,CT.OWNER_NM as ownerNm
,AES_DECRYPT(UNHEX(C.MOBILE),'1234') as mobile ,C.COUNSEL_DIV as counselDiv ,C.COUNSEL_TYPE as counselType ,C.CHANNEL_TYPE as channelType ,DATE_FORMAT(A.SCHEDULE_DT, '%Y-%m-%d') as ymd ,DATE_FORMAT(A.SCHEDULE_DT, '%H:%i') as stTime ,D.USER_NM as counselorNm ,A.COUNSEL_CD as counselCd ,A.COUNSELOR_ID as counselorId ,A.CENTER_SEQ as centerSeq ,A.USER_KEY as userKey ,A.INTAKE_CD as intakeCd ,A.CLIENT_CD as clientCd , (SELECT CENTER_NM FROM MIND_CENTER WHERE CENTER_SEQ = A.CENTER_SEQ) as centerNm , IF((SELECT ORDER_STATUS FROM MIND_ORDER WHERE COUNSEL_CD = A.COUNSEL_CD) = '100960', 'Y', 'N') as needPay ,A.RECORD_STATUS as recordStatus ,A.EXTENSION_STATUS as extensionStatus ,E.USER_NM as counselMgrNm , (SELECT USER_STATUS FROM MIND_USER WHERE USER_KEY = A.USER_KEY) as userStatus , (SELECT USE_YN FROM MIND_USER WHERE USER_KEY = A.USER_KEY) as useYn ,C.COUNSEL_RESERV_CHK ,C.RELATION ,F.RISKS AS risk
,'counselor' AS orderByNm
,'date' AS orderByNm
,'ASC' AS orderByType
,'DESC' AS orderByType
FROM MIND_COUNSEL A LEFT JOIN (SELECT AA.USER_NM, BB.CLIENT_CD FROM MIND_MGR AA LEFT JOIN MIND_CLIENT BB ON AA.USER_ID = BB.COUNSEL_MGR_ID ) as E ON A.CLIENT_CD = E.CLIENT_CD LEFT JOIN MIND_COUNSEL_RECORD as F ON A.COUNSEL_CD = F.COUNSEL_CD, MIND_COUNSEL_INTAKE C, MIND_MGR D
JOIN MIND_CENTER CT ON CT.CENTER_SEQ = #{centerSeq}
WHERE 1=1 AND A.COUNSEL_CD NOT IN ('91756', '105785')
AND A.CENTER_SEQ = #{centerSeq}
AND C.INTAKE_CD = A.INTAKE_CD AND A.STATUS in ('100520', '100521', '100884') AND A.COUNSELOR_ID = D.USER_ID
AND C.COUNSEL_NM like CONCAT ( '%',#{counselNm},'%')
AND DATE_FORMAT(A.SCHEDULE_DT, '%Y%m%d') >= #{startDt}
AND DATE_FORMAT(A.SCHEDULE_DT, '%Y%m%d') <= #{endDt}
AND D.USER_NM like CONCAT ( '%',#{counselorNm},'%')
AND A.CLIENT_CD = #{clientCd}
AND CT.OWNER_NM = #{ownerNm}
AND A.COUNSEL_CD = #{counselCd}
C.COUNSEL_NM
A.SCHEDULE_DT
ASC
DESC
) A
LIMIT #{firstIndex} , #{pageSize}

mapper.update:
UPDATE MIND_COUNSEL
	SET STATUS = #{counselStatus}
	,	COUNSEL_DT	=	DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE COUNSEL_CD = #{counselCd}

UPDATE MIND_COUNSEL
	SET STATUS = #{counselStatus}
	,	CANCEL_DT	=	DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	,	CANCEL_ID	=	#{counselorId}
WHERE COUNSEL_CD = #{counselCd}

mapper.insert:
INSERT INTO MIND_COUNSEL_PRICE (
