	, CENTER_SEQ
	, PRICE_TYPE
	, PRICE01
	, PRICE02
	, PRICE03
) VALUES (
	 #{categoryCd}
	,#{centerSeq}
	,#{priceType}
	,#{price01}
	,#{price02}
	,#{price03}
)

mapper.update:
UPDATE MIND_COUNSEL_PRICE
	SET PRICE01 = #{price01}
	,	PRICE02 = #{price02}
	,	PRICE03 = #{price03}
WHERE	CENTER_SEQ = #{centerSeq}
AND		CATEGORY_CD = #{categoryCd}

mapper.select:
SELECT COUNT(*)
FROM MIND_MGR
WHERE 1=1 
AND USE_YN = 'Y'
AND CENTER_SEQ = #{centerSeq}
AND USER_ID = #{userId}

SELECT	SCHEDULE_SEQ 
	, ST_TIME
FROM MIND_SCHEDULE
WHERE CENTER_SEQ = #{centerSeq}
	AND USER_ID = #{counselorId}
	AND YMD = ${ymd}
	AND HOLIDAY_YN = 'N'
	AND COUNSEL_CD = '0'
	AND ST_TIME >= #{stTime}
	ORDER BY ST_TIME

mapper.update:
UPDATE MIND_COUNSEL
	SET EXTENSION_STATUS = '100932'
WHERE COUNSEL_CD = #{counselCd}

mapper.insert:
INSERT INTO MIND_COUNSEL (
	  INTAKE_CD
	, CLIENT_CD
	, USER_KEY
	, CATEGORY_CD
	, CENTER_SEQ
	, SCHEDULE_SEQ
	, COUNSEL_TYPE
	, COUNSELOR_ID
	, COUNSEL_PRICE
	, STATUS
	, RECORD_STATUS
	, REG_DT
	, REG_ID
	, SCHEDULE_DT
	, SMS_DT
	, EXTENSION_NUM
) VALUES (
	#{intakeCd}
	, #{clientCd}
	, #{userKey}
	, #{categoryCd}
	, #{centerSeq}
	, #{scheduleSeq}
	, #{counselType}
	, #{counselorId}
	, #{counselPrice}
	, '100520'
	, '100693'
	, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	, #{regId}
	, #{scheduleDt}
	, #{scheduleDt}
	, #{extensionNum}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE MIND_SCHEDULE
	SET COUNSEL_CD = #{counselCd}
WHERE SCHEDULE_SEQ = #{scheduleSeq}
	AND CENTER_SEQ = #{centerSeq}
	AND YMD = #{ymd}
	AND ST_TIME = #{stTime}

UPDATE MIND_COUNSEL_INTAKE
	SET INTAKE_NUM = (SELECT COUNT(COUNSEL_CD) FROM MIND_COUNSEL WHERE INTAKE_CD = #{intakeCd})
WHERE INTAKE_CD = #{intakeCd}

UPDATE MIND_ORDER
	SET ORDER_STATUS = '100783'
WHERE ORDER_NUM IN (

		#{item.orderNum}

)

UPDATE MIND_COUNSEL
	SET STATUS = '100522'
WHERE COUNSEL_CD IN (

		#{item.counselCd}

)

UPDATE MIND_COUNSEL
   SET RECORD_STATUS = #{recordStatus}
 WHERE COUNSEL_CD = #{counselCd}
