mapper.update:
UPDATE MIND_PAY_CHECK 
SET 
OWNER_CHECK = #{userId},
OWNER_CHECK_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
WHERE CLIENT_CD = #{clientCd}
AND CENTER_SEQ = #{centerSeq}
AND COUNSEL_CD = #{counselCd}

UPDATE MIND_PAY_CHECK 
SET 
COUNSELOR_CHECK = #{userId},
COUNSELOR_CHECK_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
WHERE CLIENT_CD = #{clientCd}
AND CENTER_SEQ = #{centerSeq}
AND COUNSEL_CD = #{counselCd}

UPDATE MIND_PAY_CHECK 
SET 
MGR_CHECK = #{userId},
MGR_CHECK_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
WHERE CLIENT_CD = #{clientCd}
AND CENTER_SEQ = #{centerSeq}
AND COUNSEL_CD = #{counselCd}

mapper.insert:
INSERT INTO MIND_PAY_CHECK (
	  CLIENT_CD
	, CLIENT_NM
	, COUNSEL_CD
	, SCHEDULE_DT
	, STAFF_NM
	, MGR_NM
	, COUNSEL_TYPE
	, COUNSEL_STATUS
	, CENTER_NM			
	, CENTER_SEQ
	, COUNSELOR_NM
	, COUNSELOR_ID
	, PRICE
	, TAX_TYPE
	, BANK_NM	
	, ACCOUNT_OWNER		
) VALUES ( 
	  #{clientCd}
	, #{clientNm}
	, #{counselCd}
	, #{scheduleDt}
	, #{staffNm}
	, #{mgrNm}
	, #{counselType}
	, #{counselStatus}
	, #{centerNm}
	, #{centerSeq}
	, #{counselorNm}
	, #{counselorId}
	, #{price}
	, #{taxType}
	, #{bankNm}
	, #{accountOwner}
) ON DUPLICATE KEY UPDATE 
	COUNSEL_CD = #{counselCd}

	, CLIENT_CD = #{clientCd}


	, CLIENT_NM = #{clientNm}


	, SCHEDULE_DT = #{scheduleDt}


	, STAFF_NM = #{staffNm}


	, MGR_NM = #{mgrNm}


	, COUNSEL_TYPE = #{counselType}


	, COUNSEL_STATUS = #{counselStatus}


	, CENTER_NM = #{centerNm}


	, CENTER_SEQ = #{centerSeq}


	, COUNSELOR_NM = #{counselorNm}


	, COUNSELOR_ID = #{counselorId}


	, PRICE = #{price}


	, TAX_TYPE = #{taxType}


	, BANK_NM = #{bankNm}


	, ACCOUNT_OWNER = #{accountOwner}

mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT A.CLIENT_CD , A.CLIENT_NM , A.COUNSEL_CD , DATE_FORMAT(STR_TO_DATE(A.SCHEDULE_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS scheduleDt , A.STAFF_NM , A.MGR_NM , A.COUNSEL_TYPE , A.COUNSEL_STATUS , A.CENTER_NM , A.CENTER_SEQ , A.COUNSELOR_NM , A.COUNSELOR_ID , A.PRICE , A.TAX_TYPE , A.BANK_NM , A.ACCOUNT_OWNER , A.OWNER_CHECK AS ownerCheck , DATE_FORMAT(STR_TO_DATE(A.OWNER_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS ownerCheckDt , A.COUNSELOR_CHECK AS counselorCheck , DATE_FORMAT(STR_TO_DATE(A.COUNSELOR_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS counselorDt , A.MGR_CHECK AS managerCheck , DATE_FORMAT(STR_TO_DATE(A.MGR_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS managerDt , B.RECORD_STATUS as recordStatus FROM MIND_PAY_CHECK A LEFT OUTER JOIN MIND_COUNSEL B ON A.COUNSEL_CD = B.COUNSEL_CD WHERE 1=1
AND A.CENTER_SEQ = #{centerSeq}
AND A.COUNSELOR_ID = #{counselorId}
AND A.CLIENT_CD = #{searchClientCd}
AND A.STAFF_NM = #{searchStaffNm}
AND A.MGR_NM = #{searchMgrNm}
AND A.COUNSELOR_NM = #{searchCounselorNm}
AND A.CENTER_SEQ = #{searchCenterSeq}
AND A.SCHEDULE_DT >= #{startDt}
AND A.SCHEDULE_DT <= #{endDt}
ORDER BY A.SCHEDULE_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT CLIENT_CD , CLIENT_NM , COUNSEL_CD , DATE_FORMAT(STR_TO_DATE(SCHEDULE_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS scheduleDt , STAFF_NM , MGR_NM , COUNSEL_TYPE , COUNSEL_STATUS , CENTER_NM , CENTER_SEQ , COUNSELOR_NM , COUNSELOR_ID , PRICE , TAX_TYPE , BANK_NM , ACCOUNT_OWNER , OWNER_CHECK , DATE_FORMAT(STR_TO_DATE(OWNER_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS ownerCheckDt , COUNSELOR_CHECK , DATE_FORMAT(STR_TO_DATE(COUNSELOR_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS counselorDt , MGR_CHECK , DATE_FORMAT(STR_TO_DATE(MGR_CHECK_DT, '%Y%m%d%H%i%s'),'%Y/%m/%d %H:%i') AS managerDt FROM MIND_PAY_CHECK WHERE 1=1 AND OWNER_CHECK IS NOT NULL
AND CENTER_SEQ = #{centerSeq}
AND COUNSELOR_ID = #{counselorId}
AND CLIENT_CD = #{searchClientCd}
AND STAFF_NM = #{searchStaffNm}
AND MGR_NM = #{searchMgrNm}
AND COUNSELOR_NM = #{searchCounselorNm}
AND CENTER_SEQ = #{searchCenterSeq}
AND SCHEDULE_DT >= #{startDt}
AND SCHEDULE_DT <= #{endDt}
ORDER BY SCHEDULE_DT DESC

SELECT IFNULL(SUM(PRICE), 0)
FROM 	MIND_PAY_CHECK
WHERE	1=1

	AND		CENTER_SEQ = #{centerSeq}


	AND		COUNSELOR_ID = #{counselorId}


	AND		CLIENT_CD = #{clientCd}


	AND		STAFF_NM = #{staffNm}


	AND		MGR_NM = #{mgrNm}


	AND		COUNSELOR_NM = #{counselorNm}


	AND		CENTER_SEQ = #{searchCenterSeq}

mapper.update:
UPDATE MIND_PAY_CHECK
   SET CLIENT_CD = #{clientCd}
   		,CLIENT_NM = #{clientNm}

   		,SCHEDULE_DT = #{scheduleDt}


   		,STAFF_NM = #{staffNm}


   		,MGR_NM = #{mgrNm}


   		,COUNSEL_TYPE = #{counselType}


   		,COUNSEL_STATUS = #{counselStatus}


   		,CENTER_NM = #{centerNm}


   		,CENTER_SEQ = #{centerSeq}


   		,COUNSELOR_NM = #{counselorNm}


   		,COUNSELOR_ID = #{counselorId}


   		,PRICE = #{price}


   		,TAX_TYPE = #{taxType}


   		,BANK_NM = #{bankNm}


   		,ACCOUNT_OWNER = #{accountOwner}

WHERE COUNSEL_CD = #{counselCd}

UPDATE MIND_PAY_CHECK_CONFIRM
   SET WRITE_MONTH = #{writeMonth}
   		, WRITE_DAY = #{writeDay}
   		, ISSUE_MONTH = #{issueMonth}
   		, ISSUE_DAY = #{issueDay}
   		, ISSUE_HOUR = #{issueHour}
   		, EMAIL = #{email}
