mapper.insert:
INSERT INTO MIND_INV_CHANNEL (
	CHANNEL_CD,
	CHANNEL_NM,
	CHANNEL_DESC				
) VALUES (
	#{channelCd},
	#{channelNm},
	#{channelDesc}
)

mapper.select:
SELECT CHANNEL_CD AS channelCd , CHANNEL_NM AS channelNm , ifnull(CHANNEL_DESC, '') AS channelDesc FROM MIND_INV_CHANNEL WHERE 1=1
AND CHANNEL_CD = #{cornerCd}
ORDER BY CHANNEL_CD

SELECT COUNT(*) pageCommonCount FROM (
SELECT BANNER_SEQ AS bannerSeq , CHANNEL_CD AS channelCd , CORNER_CD AS cornerCd , BANNER_NM AS bannerNm , BANNER_IMG_URL AS bannerImgUrl , TARGET_LINK AS targetLink , START_DT AS startDt , END_DT AS endDt , DISP_ORDER AS dispOrder , USE_YN AS useYn , REG_ID AS regId , DATE_FORMAT(STR_TO_DATE(REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , MODI_ID AS modiId , DATE_FORMAT(STR_TO_DATE(MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt , SUBJECT AS subject FROM MIND_INV_BANNER WHERE 1=1
AND CORNER_CD = #{cornerCd}
AND START_DT >= #{startDt}
AND END_DT <= #{endDt}
AND BANNER_NM LIKE CONCAT (#{bannerNm},'%')
AND SUBJECT LIKE CONCAT (#{subject},'%')
AND USE_YN = #{useYn}
AND CHANNEL_CD = #{channelCd} AND CHANNEL_CD != 'MOBI'
AND CHANNEL_CD = #{mobiChannelCd}
AND CHANNEL_CD != 'MOBI'
ORDER BY REG_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

mapper.insert:
	/*
		com/ezwel/voucher/admin/service/manage/comp/mapper/ManageMapper.xml , id=insertBnr
	*/
	INSERT INTO MIND_INV_BANNER (
		CHANNEL_CD
		,CORNER_CD
		,BANNER_NM
		,BANNER_IMG_URL
		,TARGET_LINK
		,START_DT
		,END_DT

		,DISP_ORDER


   			,ALL_CLIENT_YN

		,USE_YN
		,REG_ID
		,REG_DT
		,SUBJECT

		,COMMENT

	) VALUES (
		#{channelCd}
		,#{cornerCd}
		,#{bannerNm}
		,#{fileFullPath}
		,#{targetLink}
		,#{startDt}
		,#{endDt}

		,#{dispOrder}


   			,#{allClientYn}

		,#{useYn}
		,#{regId}
		,DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
		,#{subject}

		,#{comment}

	)

mapper.update:
UPDATE MIND_INV_BANNER
   SET BANNER_NM = #{bannerNm}
   		,SUBJECT = #{subject}

   		,COMMENT = #{comment}


   		,BANNER_IMG_URL = #{fileFullPath}

   		,TARGET_LINK = #{targetLink}
   		,START_DT = #{startDt}
   		,END_DT = #{endDt}
   		,USE_YN = #{useYn}

   			,DISP_ORDER = #{dispOrder}


   			,ALL_CLIENT_YN = #{allClientYn}


   			,ALL_CLIENT_YN = 'N'

   		,MODI_ID = #{modiId}
   		,MODI_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE BANNER_SEQ = #{bannerSeq}

mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT CHANNEL_CD AS channelCd , CHANNEL_NM AS channelNm , CHANNEL_DESC AS channelDesc FROM MIND_INV_CHANNEL WHERE 1=1
AND CHANNEL_CD LIKE CONCAT('%', #{channelCd},'%')
AND CHANNEL_NM LIKE CONCAT('%', #{channelNm},'%')
) A
LIMIT #{firstIndex} , #{pageSize}

mapper.update:
UPDATE MIND_INV_CHANNEL
   SET CHANNEL_CD = #{channelCd}
   		,CHANNEL_NM = #{channelNm}
   		,CHANNEL_DESC = #{channelDesc}
WHERE CHANNEL_CD = #{channelCd}

mapper.select:
SELECT CORNER_CD AS cornerCd , CHANNEL_CD AS channelCd , CORNER_NM AS cornerNm , CORNER_DESC AS cornerDesc , DISP_NUM AS dispNum , DATE_FORMAT(STR_TO_DATE(REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , REG_ID AS regId , DATE_FORMAT(STR_TO_DATE(MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt , MODI_ID AS modiId FROM MIND_INV_CORNER WHERE 1=1
AND CORNER_CD = #{cornerCd}
AND CHANNEL_CD = #{channelCd}

mapper.update:
	UPDATE MIND_INV_CORNER
	   SET CORNER_NM = #{cornerNm}
	   	, CORNER_DESC = #{cornerDesc}
	   	, DISP_NUM = #{dispNum}
   		, MODI_ID = #{modiId}
   		, MODI_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	WHERE CORNER_CD = #{cornerCd}
	AND CHANNEL_CD = #{channelCd}

mapper.select:
SELECT BANNER_SEQ
FROM MIND_INV_BANNER
WHERE CHANNEL_CD = #{channelCd}
AND CORNER_CD = #{cornerCd}
AND BANNER_NM = #{bannerNm}
