mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT DATA_SEQ AS dataSeq ,A.BBS_CD AS bbsCd ,A.SUBJECT AS subject ,A.CONTENT AS content ,A.REQ_CD1 AS reqCd1 ,A.REQ_CD2 AS reqCd2 ,A.DISP_YN AS dispYn ,A.SERVICE_TYPE AS serviceType ,A.CLIENT_CD AS clientCd ,(SELECT CLIENT_NM FROM MIND_CLIENT WHERE CLIENT_CD = A.CLIENT_CD) AS clientNm ,A.REG_ID AS regId ,A.REG_NM AS regNm ,A.MODI_ID AS modiId ,A.MODI_NM AS modiNm ,DATE_FORMAT(STR_TO_DATE(A.MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt ,IFNULL(A.VISITED, 0) AS visited ,DATE_FORMAT(STR_TO_DATE(A.REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt ,(SELECT COMM_NM FROM MIND_COMM_CD WHERE COMM_CD = A.REQ_CD1) AS reqCd1Nm ,(SELECT COMM_NM FROM MIND_COMM_CD WHERE COMM_CD = A.REQ_CD2) AS reqCd2Nm ,(SELECT COMM_NM FROM MIND_COMM_CD WHERE COMM_CD = A.STATUS) AS statusNm FROM MIND_BBS_DATA A WHERE 1=1
AND A.SERVICE_TYPE = #{serviceType}
AND A.BBS_CD = #{bbsCd}
AND A.HIGH_DATA_SEQ = -1
AND A.CONTENT LIKE CONCAT('%', #{searchNm},'%')
AND (A.SUBJECT LIKE CONCAT('%', #{searchNm},'%') OR A.CONTENT LIKE CONCAT('%', #{searchNm},'%'))
AND A.SUBJECT LIKE CONCAT('%', #{searchNm},'%')
AND A.REQ_CD1 = #{reqCd1}
OR A.REQ_CD1 = #{reqCd1_1}
AND A.REQ_CD2 = #{reqCd2}
AND A.CLIENT_CD LIKE CONCAT ('%', #{clientCd},'%')
AND A.REG_NM LIKE CONCAT ('%', #{regNm},'%')
AND A.MODI_NM LIKE CONCAT ('%', #{regNm},'%')
AND A.DISP_YN = #{dispYn}
AND A.STATUS = #{status}
ORDER BY A.REG_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

