) as intk ) as processCsCnt, (SELECT count(*) AS cnt FROM MIND_COUNSEL A, MIND_CLIENT B, MIND_USER C, MIND_COUNSEL_INTAKE D, MIND_MGR E WHERE 1=1 AND A.CLIENT_CD = B.CLIENT_CD AND A.USER_KEY = C.USER_KEY AND A.CLIENT_CD = D.CLIENT_CD AND A.COUNSELOR_ID = E.USER_ID AND A.USER_KEY = C.USER_KEY AND A.INTAKE_CD = D.INTAKE_CD AND A.CANCEL_DT IS NULL AND A.COUNSEL_CD NOT IN ('91756', '105785')
