WHERE 1=1 AND A.COUNSEL_CD NOT IN ('91756', '105785')
AND A.CENTER_SEQ = #{centerSeq}
AND C.INTAKE_CD = A.INTAKE_CD AND A.STATUS in ('100520', '100521', '100884') AND A.COUNSELOR_ID = D.USER_ID AND G.APPR_STATUS in ('1','2','3','4','5') ) AS extenCsCnt FROM MIND_BBS_DATA MIND_BBS_DATA Limit 1

