AND B.USER_ID = #{userId}
AND B.USER_TYPE = '3001' AND C.USER_AUTH_KEY = CONCAT(B.USER_ID,'3001') AND B.MGR_STATUS in ('S') )AS waitAppCnt, (SELECT count(*) as cnt FROM MIND_COUNSEL A LEFT JOIN (SELECT File_NM,FILE_PATH,APPR_STATUS,REG_ID,REG_DT,CLIENT_CD,COUNSEL_CD,INTAKE_CD FROM MIND_COUNSEL_FILE WHERE DOC_TYPE ='2' AND DOC_STS ='1' ) as G ON A.COUNSEL_CD = G.COUNSEL_CD AND A.INTAKE_CD = G.INTAKE_CD LEFT JOIN (SELECT AA.USER_NM, BB.CLIENT_CD FROM MIND_MGR AA LEFT JOIN MIND_CLIENT BB ON AA.USER_ID = BB.COUNSEL_MGR_ID ) as E ON A.CLIENT_CD = E.CLIENT_CD LEFT JOIN MIND_COUNSEL_RECORD as F ON A.COUNSEL_CD = F.COUNSEL_CD, MIND_COUNSEL_INTAKE C, MIND_MGR D
JOIN MIND_CENTER CT ON CT.CENTER_SEQ = #{centerSeq}
WHERE 1=1 AND A.COUNSEL_CD NOT IN ('91756', '105785')
AND A.CENTER_SEQ = #{centerSeq}
AND C.INTAKE_CD = A.INTAKE_CD AND A.STATUS in ('100520', '100521', '100884') AND A.COUNSELOR_ID = D.USER_ID AND G.APPR_STATUS in ('1','2','3','4','5') ) AS extenCsCnt FROM MIND_BBS_DATA MIND_BBS_DATA Limit 1
