mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT c.client_cd as clientCd, (select client_nm from mind_client where client_cd = c.client_cd) as clientNm, c.user_nm as userNm, b.COUNSEL_NM as counselNm, b.counsel_cd as counselCd, b.counselor_id as counselId, FN_GET_COMM_NM(b.RELATION) as relation, d.center_nm as centerNm, e.user_nm as counselorNm, DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y년%m월%d일%H시') as counselDt, DATE_FORMAT(STR_TO_DATE(a.REG_DT , '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i') as regDt, a.item01Ans as item01Ans, a.item02Ans as item02Ans, a.item03Ans as item03Ans, a.item04Ans as item04Ans, a.item05Ans as item05Ans,
AES_DECRYPT(UNHEX(a.item06Ans),'1234') as item06Ans,
a.item06Ans as item06Ans,
a.item07Ans as item07Ans, a.item08Ans as item08Ans, a.item09Ans as item09Ans, a.item10Ans as item10Ans, a.target_seq as targetSeq, a.user_key as userKey, a.eval_seq as evalSeq, a.SEARCH_YN as showYn, a.SEARCH_ID as showId, a.SEARCH_DT as showDt FROM ( SELECT max(item01Ans) as item01Ans, max(item02Ans) as item02Ans, max(item03Ans) as item03Ans, max(item04Ans) as item04Ans, max(item05Ans) as item05Ans, max(item06Ans) as item06Ans, max(item07Ans) as item07Ans, max(item08Ans) as item08Ans, max(item09Ans) as item09Ans, max(item10Ans) as item10Ans, target_seq, user_key, eval_seq, reg_dt, SEARCH_YN, SEARCH_ID, SEARCH_DT FROM ( SELECT case when ITEM_SEQ=1 then ifnull(item_ans,'') else '' end as item01Ans, case when ITEM_SEQ=2 then ifnull(item_ans,'') else '' end as item02Ans, case when ITEM_SEQ=3 then ifnull(item_ans,'') else '' end as item03Ans, case when ITEM_SEQ=4 then ifnull(item_ans,'') else '' end as item04Ans, case when ITEM_SEQ=5 then ifnull(item_ans,'') else '' end as item05Ans, case when item_seq=6 then ifnull(item_ans,'') else '' end as item06Ans, case when item_seq=7 then ifnull(item_ans,'') else '' end as item07Ans, case when item_seq=8 then ifnull(item_ans,'') else '' end as item08Ans, case when item_seq=9 then ifnull(item_ans,'') else '' end as item09Ans, case when item_seq=10 then ifnull(item_ans,'') else '' end as item10Ans, target_seq, user_key, eval_seq, reg_dt, SEARCH_YN, SEARCH_ID, SEARCH_DT FROM mind_evaluation_user
WHERE eval_seq in (#{evalSeq})
) SUB GROUP BY sub.target_seq,sub.user_key ) A left join ( select aa.order_num, bb.counsel_nm, bb.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_order aa left join ( select cc.counsel_nm, cc.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_counsel bb left join mind_counsel_intake cc on bb.INTAKE_CD = cc.intake_cd ) bb on aa.COUNSEL_CD = bb.counsel_cd ) b on a.TARGET_SEQ = b.order_num left join mind_user c on a.user_key = c.user_key LEFT JOIN MIND_CENTER d ON d.center_seq=b.center_seq LEFT JOIN MIND_MGR e ON e.user_id = b.counselor_id WHERE 1=1
AND c.client_cd = #{clientCd}
AND c.user_nm = #{userNm}
AND c.user_id = #{userId}
AND b.center_seq = #{centerSeq}
AND b.counselor_id = #{counselorId}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') >= #{startMonth}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') <= #{endMonth}
AND a.item01Ans in (${item01Ans})
AND a.item02Ans in (${item02Ans})
AND a.SEARCH_YN = #{showYn}
ORDER BY a.reg_dt desc
) X
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
SELECT c.client_cd as clientCd, (select client_nm from mind_client where client_cd = c.client_cd) as clientNm, c.user_nm as userNm, b.COUNSEL_NM as counselNm, b.counsel_cd as counselCd, b.counselor_id as counselId, FN_GET_COMM_NM(b.RELATION) as relation, d.center_nm as centerNm, e.user_nm as counselorNm, DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y년%m월%d일%H시') as counselDt, DATE_FORMAT(STR_TO_DATE(a.REG_DT , '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i') as regDt, a.item01Ans as item01Ans, a.item02Ans as item02Ans, a.item03Ans as item03Ans, a.item04Ans as item04Ans, a.item05Ans as item05Ans, a.item06Ans as item06Ans, AES_DECRYPT(UNHEX(a.item06Ans),'1234') as item07Ans, a.item08Ans as item08Ans, a.item09Ans as item09Ans, a.item10Ans as item10Ans, a.target_seq as targetSeq, a.user_key as userKey, a.eval_seq as evalSeq FROM ( SELECT max(item01Ans) as item01Ans, max(item02Ans) as item02Ans, max(item03Ans) as item03Ans, max(item04Ans) as item04Ans, max(item05Ans) as item05Ans, max(item06Ans) as item06Ans, max(item07Ans) as item07Ans, max(item08Ans) as item08Ans, max(item09Ans) as item09Ans, max(item10Ans) as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM ( SELECT case when ITEM_SEQ=1 then ifnull(item_ans,'') else '' end as item01Ans, case when ITEM_SEQ=2 then ifnull(item_ans,'') else '' end as item02Ans, case when ITEM_SEQ=3 then ifnull(item_ans,'') else '' end as item03Ans, case when ITEM_SEQ=4 then ifnull(item_ans,'') else '' end as item04Ans, case when ITEM_SEQ=5 then ifnull(item_ans,'') else '' end as item05Ans, case when item_seq=6 then ifnull(item_ans,'') else '' end as item06Ans, case when item_seq=7 then ifnull(item_ans,'') else '' end as item07Ans, case when item_seq=8 then ifnull(item_ans,'') else '' end as item08Ans, case when item_seq=9 then ifnull(item_ans,'') else '' end as item09Ans, case when item_seq=10 then ifnull(item_ans,'') else '' end as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM mind_evaluation_user
WHERE eval_seq in (1,6) and SEARCH_YN ='Y'
) SUB GROUP BY sub.target_seq,sub.user_key ) A left join ( select aa.order_num, bb.counsel_nm, bb.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_order aa left join ( select cc.counsel_nm, cc.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_counsel bb left join mind_counsel_intake cc on bb.INTAKE_CD = cc.intake_cd ) bb on aa.COUNSEL_CD = bb.counsel_cd ) b on a.TARGET_SEQ = b.order_num left join mind_user c on a.user_key = c.user_key LEFT JOIN MIND_CENTER d ON d.center_seq=b.center_seq LEFT JOIN MIND_MGR e ON e.user_id = b.counselor_id WHERE 1=1
AND c.client_cd = #{clientCd}
AND c.user_nm = #{userNm}
AND c.user_id = #{userId}
AND b.center_seq = #{centerSeq}
AND b.counselor_id = #{counselorId}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') >= #{startMonth}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') <= #{endMonth}
ORDER BY a.reg_dt desc
) X
LIMIT #{firstIndex} , #{pageSize}

SELECT c.client_cd as clientCd, (select client_nm from mind_client where client_cd = c.client_cd) as clientNm, c.user_nm as userNm, b.COUNSEL_NM as counselNm, b.counsel_cd as counselCd, FN_GET_COMM_NM(b.RELATION) as relation, d.center_nm as centerNm, e.user_nm as counselorNm, DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y년%m월%d일%H시') as counselDt, DATE_FORMAT(STR_TO_DATE(a.REG_DT , '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i') as regDt, a.item01Ans as item01Ans, a.item02Ans as item02Ans, a.item03Ans as item03Ans, a.item04Ans as item04Ans, a.item05Ans as item05Ans,
AES_DECRYPT(UNHEX(a.item06Ans),'1234') as item06Ans,
a.item06Ans as item06Ans,
a.item07Ans as item07Ans, a.item08Ans as item08Ans, a.item09Ans as item09Ans, a.item10Ans as item10Ans, a.target_seq as targetSeq, a.user_key, a.eval_seq as evalSeq FROM ( SELECT max(item01Ans) as item01Ans, max(item02Ans) as item02Ans, max(item03Ans) as item03Ans, max(item04Ans) as item04Ans, max(item05Ans) as item05Ans, max(item06Ans) as item06Ans, max(item07Ans) as item07Ans, max(item08Ans) as item08Ans, max(item09Ans) as item09Ans, max(item10Ans) as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM ( SELECT case when ITEM_SEQ=1 then ifnull(item_ans,'') else '' end as item01Ans, case when ITEM_SEQ=2 then ifnull(item_ans,'') else '' end as item02Ans, case when ITEM_SEQ=3 then ifnull(item_ans,'') else '' end as item03Ans, case when ITEM_SEQ=4 then ifnull(item_ans,'') else '' end as item04Ans, case when ITEM_SEQ=5 then ifnull(item_ans,'') else '' end as item05Ans, case when item_seq=6 then ifnull(item_ans,'') else '' end as item06Ans, case when item_seq=7 then ifnull(item_ans,'') else '' end as item07Ans, case when item_seq=8 then ifnull(item_ans,'') else '' end as item08Ans, case when item_seq=9 then ifnull(item_ans,'') else '' end as item09Ans, case when item_seq=10 then ifnull(item_ans,'') else '' end as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM mind_evaluation_user
WHERE eval_seq = #{evalSeq}
) SUB GROUP BY sub.target_seq,sub.user_key ) A left join ( select aa.order_num, bb.counsel_nm, bb.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_order aa left join ( select cc.counsel_nm, cc.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_counsel bb left join mind_counsel_intake cc on bb.INTAKE_CD = cc.intake_cd ) bb on aa.COUNSEL_CD = bb.counsel_cd ) b on a.TARGET_SEQ = b.order_num left join mind_user c on a.user_key = c.user_key LEFT JOIN MIND_CENTER d ON d.center_seq=b.center_seq LEFT JOIN MIND_MGR e ON e.user_id = b.counselor_id WHERE 1=1
AND c.client_cd = #{clientCd}
AND c.user_nm = #{userNm}
AND c.user_id = #{userId}
AND b.center_seq = #{centerSeq}
AND b.counselor_id = #{counselorId}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') >= #{startMonth}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') <= #{endMonth}
ORDER BY a.reg_dt desc

SELECT
ROUND(AVG(a.item01Ans), 2) as itemAvg, MIN(a.item01Ans) as itemMin, MAX(a.item01Ans) as itemMax
ROUND(AVG(a.item02Ans), 2) as itemAvg, MIN(a.item02Ans) as itemMin, MAX(a.item02Ans) as itemMax
FROM ( SELECT max(item01Ans) as item01Ans, max(item02Ans) as item02Ans, max(item03Ans) as item03Ans, max(item04Ans) as item04Ans, max(item05Ans) as item05Ans, max(item06Ans) as item06Ans, max(item07Ans) as item07Ans, max(item08Ans) as item08Ans, max(item09Ans) as item09Ans, max(item10Ans) as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM ( SELECT case when ITEM_SEQ=1 then ifnull(item_ans,'') else '' end as item01Ans, case when ITEM_SEQ=2 then ifnull(item_ans,'') else '' end as item02Ans, case when ITEM_SEQ=3 then ifnull(item_ans,'') else '' end as item03Ans, case when ITEM_SEQ=4 then ifnull(item_ans,'') else '' end as item04Ans, case when ITEM_SEQ=5 then ifnull(item_ans,'') else '' end as item05Ans, case when item_seq=6 then ifnull(item_ans,'') else '' end as item06Ans, case when item_seq=7 then ifnull(item_ans,'') else '' end as item07Ans, case when item_seq=8 then ifnull(item_ans,'') else '' end as item08Ans, case when item_seq=9 then ifnull(item_ans,'') else '' end as item09Ans, case when item_seq=10 then ifnull(item_ans,'') else '' end as item10Ans, target_seq, user_key, eval_seq, reg_dt FROM mind_evaluation_user WHERE 1=1
and eval_seq = #{evalSeq}
and SEARCH_YN ='Y' ) SUB GROUP BY sub.target_seq,sub.user_key ) A left join ( select aa.order_num, bb.counsel_nm, bb.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_order aa left join ( select cc.counsel_nm, cc.relation, bb.schedule_dt, bb.counsel_cd ,bb.center_seq, bb.counselor_id from mind_counsel bb left join mind_counsel_intake cc on bb.INTAKE_CD = cc.intake_cd ) bb on aa.COUNSEL_CD = bb.counsel_cd ) b on a.TARGET_SEQ = b.order_num left join mind_user c on a.user_key = c.user_key LEFT JOIN MIND_CENTER d ON d.center_seq=b.center_seq LEFT JOIN MIND_MGR e ON e.user_id = b.counselor_id WHERE 1=1
AND A.item01Ans != 'null'
AND A.item02Ans != ''
AND c.client_cd = #{clientCd}
AND c.user_nm = #{userNm}
AND c.user_id = #{userId}
AND b.center_seq = #{centerSeq}
AND b.counselor_id = #{counselorId}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') >= #{startMonth}
AND DATE_FORMAT(STR_TO_DATE(b.SCHEDULE_DT , '%Y%m%d%H%i%s'),'%Y/%m/%d') <= #{endMonth}
ORDER BY a.reg_dt desc

select EVAL_SEQ AS evalSeq, ITEM_SEQ AS itemSeq, ITEM_NO AS itemNo, ITEM_TITLE AS itemTitle, ITEM_TYPE AS itemType, ITEM_LIMIT AS itemLimit, ITEM_YN AS itemYn from mind_evaluation_item where 1=1
and eval_seq = #{evalSeq}
order by item_seq

mapper.delete:
delete from mind_evaluation_user
where 
1=1		
and target_seq = #{targetSeq}
and eval_seq = #{evalSeq}

mapper.update:
update mind_counsel_intake
	set survey_call_yn = 'Y'
where intake_cd = #{intakeCd}
