SELECT COUNT(*) pageCommonCount FROM (
SELECT B.USER_NM AS userNm , B.USER_ID AS userId ,B.MOBILE AS mobile , (CASE WHEN SUBSTRING(B.RRN,8,1) <= 2 THEN (DATE_FORMAT(NOW(), '%Y') - CONCAT('19', SUBSTRING(B.RRN,1,2))+1) WHEN 2 < SUBSTRING(B.RRN,8,1) <= 4 THEN (DATE_FORMAT(NOW(), '%Y') - CONCAT('20', SUBSTRING(B.RRN,1,2))+1) END) AS rrn , B.CENTER_SEQ AS centerSeq , (CASE WHEN A.GENDER = 'M' THEN '남' WHEN A.GENDER = 'F' THEN '여' END) AS gender , (CASE WHEN B.MGR_STATUS = 'Y' THEN '승인' WHEN B.MGR_STATUS = 'N' THEN '중지' WHEN B.MGR_STATUS = 'S' THEN '대기' WHEN B.MGR_STATUS = 'R' THEN '반려' END) AS mgrStatus , (CASE WHEN A.WORK_TYPE = 'S' THEN '상주' WHEN A.WORK_TYPE = 'P' THEN '파트타임' END) AS workType , A.CHANNEL_TYPE AS channelType , DATE_FORMAT(STR_TO_DATE(B.MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt , DATE_FORMAT(STR_TO_DATE(B.REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , (CASE WHEN B.USE_YN = 'Y' THEN '사용함' WHEN B.USE_YN = 'N' THEN '사용중지' END) AS useYn , (CASE WHEN C.AUTH_CD = 'ROLE_PARTNER_CENTER' THEN '센터장' WHEN C.AUTH_CD = 'ROLE_PARTNER_SANGDAM' THEN '상담' WHEN C.AUTH_CD = 'ROLE_PARTNER_SEND' THEN '파견' WHEN C.AUTH_CD = 'ROLE_PARTNER_GROUP' THEN '집단' WHEN C.AUTH_CD = 'ROLE_PARTNER_TEACHER' THEN '강사' END) AS authCd FROM MIND_MGR_SUB A , MIND_MGR B , ACL_USER_AUTH C WHERE 1=1 AND A.USER_ID = B.USER_ID AND B.CENTER_SEQ = #{centerSeq} AND B.USER_TYPE = '3001' AND C.USER_AUTH_KEY = CONCAT(B.USER_ID,'3001')
AND B.USER_NM LIKE CONCAT('%', #{userNm},'%')
AND B.MGR_STATUS in (${sMStatus})
AND A.CHANNEL_TYPE REGEXP #{channelType}
AND A.WORK_TYPE in (${sWType})
AND DATE_FORMAT(CONVERT(B.MODI_DT, DATE), '%Y%m%d') >= #{startDt}
AND DATE_FORMAT(CONVERT(B.MODI_DT, DATE), '%Y%m%d') <= #{endDt}
AND B.MOBILE LIKE CONCAT('%', #{mobile},'%')
AND C.AUTH_CD in
#{auth}
ORDER BY B.REG_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

