SELECT B.USER_NM AS userNm , B.USER_ID AS userId , (CASE WHEN SUBSTRING(B.RRN,8,1) <= 2 THEN (DATE_FORMAT(NOW(), '%Y') - CONCAT('19', SUBSTRING(B.RRN,1,2))+1) WHEN 2 < SUBSTRING(B.RRN,8,1) <= 4 THEN (DATE_FORMAT(NOW(), '%Y') - CONCAT('20', SUBSTRING(B.RRN,1,2))+1) END) AS rrn , (CASE WHEN A.GENDER = 'M' THEN '남' WHEN A.GENDER = 'F' THEN '여' END) AS gender , (CASE WHEN B.MGR_STATUS = 'Y' THEN '승인' WHEN B.MGR_STATUS = 'N' THEN '중지' WHEN B.MGR_STATUS = 'S' THEN '대기' WHEN B.MGR_STATUS = 'R' THEN '반려' END) AS mgrStatus , (CASE WHEN A.WORK_TYPE = 'S' THEN '상주' WHEN A.WORK_TYPE = 'P' THEN '파트타임' END) AS workType , A.CHANNEL_TYPE AS channelType , DATE_FORMAT(STR_TO_DATE(B.MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt , DATE_FORMAT(STR_TO_DATE(B.REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , (CASE WHEN B.USE_YN = 'Y' THEN '사용함' WHEN B.USE_YN = 'N' THEN '사용중지' END) AS useYn , C.CENTER_NM AS centerNm , A.Mgr_EVAL_GRADE AS mgrEvalGrade FROM MIND_MGR_SUB A , MIND_MGR B, MIND_CENTER C WHERE 1=1 AND B.USER_TYPE = '3001' AND B.USE_YN = 'Y' AND A.USER_ID = B.USER_ID AND C.CENTER_SEQ = B.CENTER_SEQ
AND B.USER_NM LIKE CONCAT('%', #{userNm},'%')
AND C.CENTER_SEQ = #{centerSeq}
ORDER BY B.USER_NM DESC
) A
LIMIT #{firstIndex} , #{pageSize}

