SELECT COUNT(*) pageCommonCount FROM (
SELECT B.USER_NM AS userNm , B.USER_ID AS userId , B.RRN AS rrn , B.MOBILE AS mobile , (CASE WHEN A.GENDER = 'M' THEN '남' WHEN A.GENDER = 'F' THEN '여' END) AS gender , (CASE WHEN B.MGR_STATUS = 'Y' THEN '승인' WHEN B.MGR_STATUS = 'N' THEN '중지' WHEN B.MGR_STATUS = 'S' THEN '대기' WHEN B.MGR_STATUS = 'R' THEN '반려' END) AS mgrStatus , (SELECT USER_NM from MIND_MGR where USER_ID=B.MODI_ID ) AS workType , A.CHANNEL_TYPE AS channelType , DATE_FORMAT(STR_TO_DATE(B.MODI_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS modiDt , DATE_FORMAT(STR_TO_DATE(B.REG_DT, '%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS regDt , (CASE WHEN B.USE_YN = 'Y' THEN '사용함' WHEN B.USE_YN = 'N' THEN '사용중지' END) AS useYn , C.CENTER_NM AS centerNm , (CASE WHEN D.AUTH_CD = 'ROLE_P_CENTER_HUE' THEN '센터장' WHEN D.AUTH_CD = 'ROLE_P_SANGDAM_HUE' THEN '상담사' END) AS authCd
FROM MIND_MGR_SUB A , MIND_MGR B, MIND_CENTER C , ACL_USER_AUTH D WHERE 1=1 AND B.USER_TYPE = '3001' AND B.USE_YN = 'Y' AND B.MGR_FLAG ='C' AND A.USER_ID = B.USER_ID AND C.CENTER_SEQ IN (select CENTER_SEQ from hue_center) AND C.CENTER_SEQ = B.CENTER_SEQ AND D.USER_AUTH_KEY = CONCAT(B.USER_ID,'3001')
AND B.USER_NM LIKE CONCAT('%', #{userNm},'%')
AND C.CENTER_NM = #{centerNm}
AND B.MGR_STATUS in (${sMStatus})
AND A.CHANNEL_TYPE REGEXP #{channelType}
AND B.RRN LIKE CONCAT('%', #{userRrn},'%')
AND B.MOBILE LIKE CONCAT('%', #{userMobile},'%')
AND ( B.MODI_ID IN(SELECT USER_ID FROM MIND_MGR WHERE USER_NM LIKE CONCAT('%', #{userMnm},'%') ) )
AND A.GENDER = #{userGender}
AND A.WORK_TYPE in (${sWType})
AND DATE_FORMAT(CONVERT(B.MODI_DT, DATE), '%Y%m%d') >= #{startDt}
AND DATE_FORMAT(CONVERT(B.MODI_DT, DATE), '%Y%m%d') <= #{endDt}
AND D.AUTH_CD = #{authCd}
ORDER BY B.REG_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

