SELECT COUNT(*) pageCommonCount FROM (
SELECT a.user_id AS userId, a.user_nm AS userNm, a.rrn AS rrn, (select center_nm from mind_center where center_seq = a.center_seq) AS centerNm, case(b.EDUCATION) when 'D' then '박사' when 'M' then '석사' else b.CAREER end AS education, b.UNIVERSITY AS university, b.DEPARTMENT AS department, b.CAREER AS career, (SELECT group_concat(distinct FN_GET_COMM_NM(cert_cd)) from mind_mgr_cert where user_id = a.user_id) AS cert, (SELECT group_concat(etc) from mind_mgr_cert where user_id = a.user_id) AS etcCert, a.use_yn AS useYn, (CASE WHEN D.AUTH_CD = 'ROLE_PARTNER_CENTER' THEN '센터장' WHEN D.AUTH_CD = 'ROLE_PARTNER_SANGDAM' THEN '상담' WHEN D.AUTH_CD = 'ROLE_PARTNER_SEND' THEN '파견' WHEN D.AUTH_CD = 'ROLE_PARTNER_GROUP' THEN '집단' WHEN D.AUTH_CD = 'ROLE_PARTNER_TEACHER' THEN '강사' END) AS authCd FROM (select * from mind_mgr where user_type = '3001' ) as a left join mind_mgr_sub b on a.USER_ID = b.USER_ID , ACL_USER_AUTH AS D WHERE 1=1 AND a.user_id not in ('32827980', 'b3817', 'bookmount', 'hkinternational', 'test10234') AND a.center_seq is not null AND D.USER_AUTH_KEY = CONCAT(A.USER_ID,'3001')
AND a.USER_NM LIKE CONCAT('%', #{userNm},'%')
AND a.CENTER_SEQ = #{searchCenterSeq}
ORDER BY a.center_seq, a.USER_ID
) A
LIMIT #{firstIndex} , #{pageSize}

