mapper.select:
SELECT COUNT(*) pageCommonCount FROM (
SELECT A.COUNSEL_CD AS counselCd , A.USER_KEY AS userKey , B.CLIENT_NM AS clientNm , C.USER_NM AS userNm , D.COUNSEL_NM AS counselNm , D.COUNSEL_DIV AS counselDiv , D.COUNSEL_TYPE AS counselType , E.USER_NM AS counselorNm , D.INTAKE_NUM AS intakeNum , A.RECORD_STATUS as recordStatus , A.INTAKE_CD AS intakeCd , IFNULL(A.EXTENSION_NUM, '1') AS extensionNum , DATE_FORMAT(STR_TO_DATE(A.SCHEDULE_DT, '%Y%m%d%H%i%s'),'%Y년%m월%d일%H시') as scheduleDt FROM MIND_COUNSEL A, MIND_CLIENT B, MIND_USER C, MIND_COUNSEL_INTAKE D, MIND_MGR E WHERE 1=1 AND A.CLIENT_CD = B.CLIENT_CD AND A.USER_KEY = C.USER_KEY AND A.CLIENT_CD = D.CLIENT_CD AND A.COUNSELOR_ID = E.USER_ID AND A.USER_KEY = C.USER_KEY AND A.INTAKE_CD = D.INTAKE_CD AND A.CANCEL_DT IS NULL AND A.COUNSEL_CD NOT IN ('91756', '105785')
AND E.CENTER_SEQ = #{centerSeq}
AND A.COUNSEL_CD = #{counselCd}
AND (A.COUNSEL_TYPE = 100435 OR A.RECORD_CHECK_YN = 'Y')
AND B.CLIENT_NM = #{clientNm}
AND C.USER_NM = #{userNm}
AND D.COUNSEL_NM = #{counselNm}
AND E.USER_NM = #{counselorNm}
AND A.RECORD_STATUS REGEXP #{recordStatus}
AND E.USER_ID = #{loginId}
ORDER BY A.SCHEDULE_DT DESC
) A
LIMIT #{firstIndex} , #{pageSize}

SELECT COUNT(*) pageCommonCount FROM (
SELECT B.COUNSEL_CD AS counselCd , A.INTAKE_CD AS intakeCd , A.INTAKE_NUM AS intakeNum , A.CLIENT_CD AS clientCd , B.USER_KEY AS userKey , C.USER_NM AS userNm , A.COUNSEL_NM AS counselNm , A.COUNSEL_DIV AS counselDiv , A.COUNSEL_TYPE AS counselType , B.COUNSELOR_ID AS counselorId , D.USER_NM AS counselorNm , E.CLIENT_NM AS clientNm , B.RECORD_STATUS AS recordStatus , F.RECORD_CD AS recordCd , F.RISKS AS risks FROM MIND_COUNSEL B LEFT OUTER JOIN MIND_COUNSEL_INTAKE A ON B.INTAKE_CD = A.INTAKE_CD AND B.CLIENT_CD = A.CLIENT_CD LEFT OUTER JOIN MIND_USER C ON B.USER_KEY = C.USER_KEY LEFT OUTER JOIN MIND_MGR D ON B.COUNSELOR_ID = D.USER_ID LEFT OUTER JOIN MIND_COUNSEL_RECORD F ON B.COUNSEL_CD = F.COUNSEL_CD LEFT OUTER JOIN MIND_CLIENT E ON A.CLIENT_CD = E.CLIENT_CD WHERE 1=1
AND B.USER_KEY = #{userKey}
AND B.COUNSELOR_ID = #{loginId}
ORDER BY B.INTAKE_CD DESC
) AA

SELECT A.INTAKE_CD AS intakeCd , A.CLIENT_CD AS clientCd , DATE_FORMAT(STR_TO_DATE(A.RRN, '%Y%m%d'),'%Y-%m-%d') AS rrn , (CASE WHEN A.GENDER = 'M' THEN '남' WHEN A.GENDER = 'F' THEN '여' WHEN A.GENDER IS NULL THEN '-' END) AS gender , A.COUNSEL_NM AS counselNm /* 성명 내담당자명 동일 */ , A.EDUCATION AS education , A.SESSION AS session , A.JOB AS job , AES_DECRYPT(UNHEX(A.MOBILE),'1234') AS mobile , A.EMAIL AS email , A.COUNSEL_DIV AS counselDiv , FN_GET_CATEGORY_NM(A.COUNSEL_DIV) AS counselDivNm , FN_GET_CATEGORY_NM(A.CHANNEL_TYPE) AS channelNm , A.COUNSEL_DIV AS counselDivNm , A.CHANNEL_TYPE AS channelNm , A.CHANNEL_TYPE AS channelType , A.COUNSEL_TYPE AS counselType , A.CAUSE AS cause , A.MEMO AS memo , B.COUNSEL_CD AS counselCd , A.INTAKE_NUM AS intakeNumber , A.RELATION AS relation ,(select File_NM from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='1') as filename1 ,(select File_NM from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='2') as filename2 ,(select File_NM from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='3') as filename3 ,(select File_NM from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='4') as filename4 ,(select File_NM from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='5') as filename5 ,(select FILE_PATH from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='1') as filefath1 ,(select FILE_PATH from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='2') as filefath2 ,(select FILE_PATH from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='3') as filefath3 ,(select FILE_PATH from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='4') as filefath4 ,(select FILE_PATH from MIND_COUNSEL_FILE where INTAKE_CD = B.INTAKE_CD and DOC_TYPE ='1' AND DOC_STS ='5') as filefath5 FROM MIND_COUNSEL_INTAKE A LEFT OUTER JOIN MIND_COUNSEL B ON A.USER_KEY = B.USER_KEY AND A.CLIENT_CD = B.CLIENT_CD AND A.INTAKE_CD = B.INTAKE_CD WHERE 1=1
AND B.COUNSEL_CD = #{counselCd}
AND B.USER_KEY = #{userKey}

