mapper.update:
UPDATE MIND_COUNSEL
	SET RECORD_STATUS = #{recordStatus}
	  , STATUS = #{counselStatus}

	  , EXTENSION_STATUS = #{extensionStatus}

WHERE COUNSEL_CD = #{counselCd}

UPDATE MIND_COUNSEL_PLAN
	SET MODI_ID  		= #{modiId}
		, MODI_DT  		= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')

		, FINAL_GOAL		= #{finalGoal}


		, COURSE_GOAL	= #{courseGoal}


		, COUNSEL_PLAN	= #{counselPlan}

WHERE	INTAKE_CD		= #{intakeCd}
AND		CLIENT_CD		= #{clientCd}

UPDATE MIND_COUNSEL_RECORD
	SET	  MODI_ID  		= #{modiId}
		, MODI_DT  		= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
		, SAFETY_QUESTION1 = #{q1}
		, SAFETY_QUESTION2 = #{q2}
		, SAFETY_QUESTION3 = #{q3}
		, SAFETY_QUESTION4 = #{q4}
		, SAFETY_QUESTION5 = #{q5}
		, SAFETY_QUESTION6 = #{q6}
		, SAFETY_QUESTION7 = #{q7}
		, SAFETY_QUESTION8 = #{q8}
		, SAFETY_QUESTION9 = #{q9}
		, SAFETY_QUESTION10 = #{q10}
		, ORS_QUESTION1 = #{q11}
		, ORS_QUESTION2 = #{q12}
		, ORS_QUESTION3 = #{q13}
		, ORS_QUESTION4 = #{q14}

		, END_TYPE = #{endType}


		, END_REASON_TYPE = #{endReasonType}


		, ETC_END_REASON = #{etcEndReason}


		, END_REPORT = #{endReport}


		, SUBJECT			= #{subject}


		, MAIN_ISSUE	= #{mainIssue}


		, GOAL			= #{goal}


		, INTERVENTION	= #{intervention}


		, FEEDBACK		= #{feedback}

		, MEMO		= #{recordMemo}

		, TIME		= #{time}


		, FILE_NM		= #{fileNm}


		, FILE_PATH		= #{filePath}


		, RISKS		= #{risks}


		, RISK_REASON		= #{riskReason}


		, SUBJECT_ITEM_CD1		= #{subjectItemCd1}


		, SUBJECT_ITEM_CD2		= #{subjectItemCd2}


		, SUBJECT_ITEM_CD3		= #{subjectItemCd3}

WHERE	RECORD_CD		= #{recordCd}
AND		COUNSEL_CD		= #{counselCd}

UPDATE MIND_COUNSEL_RECORD_TXT
	SET CONTENT			= #{content}
WHERE	RECORD_CD		= #{recordCd}
AND		RECORD_TYPE		= #{recordType}

